import generate from '../src/generate';
import { Template, BLANK_PDF, Schema } from '@pdfme/common';
import { getFont, pdfToImages } from './utils';
import 'jest-image-snapshot';

describe('generate integrate test', () => {
  describe('basic generator', () => {
    const textObject = (x: number, y: number, name: string = 'a'): Schema => ({
      name,
      type: 'text',
      content: '',
      position: { x, y },
      width: 100,
      height: 100,
      fontSize: 13,
    });

    const singleSchemaTemplate: Template = {
      basePdf: BLANK_PDF,
      schemas: [[textObject(0, 0), textObject(25, 25, 'b')]],
    };

    const multiSchemasTemplate: Template = {
      basePdf:
        'data:application/pdf;base64,',
      schemas: [[textObject(0, 0)], [textObject(25, 25, 'b')]],
    };

    const singleInputs = [{ a: 'a', b: 'b' }];
    const multiInputs = [
      { a: 'a-1', b: 'b-1' },
      { a: 'a-2', b: 'b-2' },
    ];

    const testCases = [
      {
        template: singleSchemaTemplate,
        inputs: singleInputs,
        testName: 'singleSchemaTemplate with singleInputs',
      },
      {
        template: singleSchemaTemplate,
        inputs: multiInputs,
        testName: 'singleSchemaTemplate with multiInputs',
      },
      {
        template: multiSchemasTemplate,
        inputs: singleInputs,
        testName: 'multiSchemasTemplate with singleInputs',
      },
      {
        template: multiSchemasTemplate,
        inputs: multiInputs,
        testName: 'multiSchemasTemplate with multiInputs',
      },
    ];

    // testCases for
    for (let i = 0; i < testCases.length; i += 1) {
      const { template, inputs, testName } = testCases[i];
      test(testName, async () => {
        const pdf = await generate({ inputs, template });
        const images = await pdfToImages(pdf);
        for (let i = 0; i < images.length; i++) {
          expect(images[i]).toMatchImageSnapshot({
            customSnapshotIdentifier: `${testName}-${i + 1}`,
          });
        }
      });
    }
  });

  describe('use fontColor template', () => {
    test(`sample`, async () => {
      const inputs = [{ name: 'here is purple color' }];
      const template: Template = {
        basePdf: BLANK_PDF,
        schemas: [
          [
            {
              name: 'name',
              type: 'text',
              content: '',
              position: { x: 30, y: 30 },
              width: 100,
              height: 20,
              fontColor: '#7d2ae8',
            },
          ],
        ],
      };
      const pdf = await generate({ inputs, template });
      const images = await pdfToImages(pdf);
      for (let i = 0; i < images.length; i++) {
        expect(images[i]).toMatchImageSnapshot({
          customSnapshotIdentifier: `fontColor-${i + 1}`,
        });
      }
    });
  });

  describe('use fontSubset template', () => {
    test(`sample`, async () => {
      const inputs = [{ field1: 'SauceHanSansJP', field2: 'SauceHanSerifJP' }];
      const template: Template = {
        basePdf: BLANK_PDF,
        schemas: [
          [
            {
              name: 'field1',
              type: 'text',
              content: '',
              position: { x: 30, y: 30 },
              width: 100,
              height: 20,
              fontName: 'SauceHanSansJP',
            },
            {
              name: 'field2',
              type: 'text',
              content: '',
              position: { x: 60, y: 60 },
              width: 100,
              height: 20,
              fontName: 'SauceHanSerifJP',
            },
          ],
        ],
      };
      jest.setTimeout(30000);
      const { SauceHanSansJP, SauceHanSerifJP } = getFont();
      const pdf = await generate({
        inputs,
        template,
        options: {
          font: {
            SauceHanSansJP: {
              data: SauceHanSansJP.data,
              fallback: true,
              subset: false,
            },
            SauceHanSerifJP: {
              data: SauceHanSerifJP.data,
              subset: false,
            },
          },
        },
      });
      const images = await pdfToImages(pdf);
      for (let i = 0; i < images.length; i++) {
        expect(images[i]).toMatchImageSnapshot({
          customSnapshotIdentifier: `fontSubset-${i + 1}`,
        });
      }
    }, 10000);
  });
});

describe('check validation', () => {
  test(`inputs length is 0`, async () => {
    const inputs: { [key: string]: string }[] = [];
    const template: Template = {
      basePdf: BLANK_PDF,
      schemas: [
        [
          {
            name: 'a',
            type: 'text',
            content: '',
            position: { x: 0, y: 0 },
            width: 100,
            height: 100,
          },
        ],
      ],
    };
    try {
      await generate({ inputs, template, options: { font: getFont() } });
      fail();
    } catch (e: any) {
      expect(e.message).toEqual(`[@pdfme/common] Invalid argument:
--------------------------
ERROR POSITION: inputs
ERROR MESSAGE: Array must contain at least 1 element(s)
--------------------------`);
    }
  });
  test(`missing fallback font`, async () => {
    const inputs = [{ a: 'test' }];
    const template: Template = {
      basePdf: BLANK_PDF,
      schemas: [
        [
          {
            name: 'a',
            type: 'text',
            content: '',
            position: { x: 0, y: 0 },
            width: 100,
            height: 100,
          },
        ],
      ],
    };
    const font = getFont();
    font.Roboto.fallback = false;
    font.SauceHanSansJP.fallback = false;
    font.SauceHanSerifJP.fallback = false;
    try {
      await generate({ inputs, template, options: { font } });
      fail();
    } catch (e: any) {
      expect(e.message).toEqual(
        `[@pdfme/common] fallback flag is not found in font. true fallback flag must be only one.
Check this document: https://pdfme.com/docs/custom-fonts#about-font-type`
      );
    }
  });
  test(`too many fallback font`, async () => {
    const inputs = [{ a: 'test' }];
    const template: Template = {
      basePdf: BLANK_PDF,
      schemas: [
        [
          {
            name: 'a',
            type: 'text',
            content: '',
            position: { x: 0, y: 0 },
            width: 100,
            height: 100,
          },
        ],
      ],
    };
    const font = getFont();
    font.Roboto.fallback = false;
    font.SauceHanSansJP.fallback = true;
    font.SauceHanSerifJP.fallback = true;
    try {
      await generate({ inputs, template, options: { font } });
      fail();
    } catch (e: any) {
      expect(e.message).toEqual(
        `[@pdfme/common] 2 fallback flags found in font. true fallback flag must be only one.
Check this document: https://pdfme.com/docs/custom-fonts#about-font-type`
      );
    }
  });
  test(`missing font in template.schemas`, async () => {
    const inputs = [{ a: 'test' }];
    const template: Template = {
      basePdf: BLANK_PDF,
      schemas: [
        [
          {
            name: 'a',
            type: 'text',
            content: '',
            fontName: 'SauceHanSansJP2',
            position: { x: 0, y: 0 },
            width: 100,
            height: 100,
          },
          {
            name: 'b',
            type: 'text',
            content: '',
            position: { x: 0, y: 0 },
            width: 100,
            height: 100,
          },
        ],
      ],
    };
    try {
      await generate({ inputs, template, options: { font: getFont() } });
      fail();
    } catch (e: any) {
      expect(e.message).toEqual(
        `[@pdfme/common] SauceHanSansJP2 of template.schemas is not found in font.
Check this document: https://pdfme.com/docs/custom-fonts`
      );
    }
  });
});
